export default function Profile({ mustVerifyEmail, status }: { mustVerifyEmail: boolean; status?: string }) {
    const { auth, demo_mode } = usePage<SharedData & { demo_mode?: boolean }>().props;
    const [resetting, setResetting] = useState(false);
    const [resetStatus, setResetStatus] = useState<string | null>(null);

    const { data, setData, patch, errors, processing, recentlySuccessful } = useForm<Required<ProfileForm>>({
        name: auth.user.name,
        email: auth.user.email,
        timezone: auth.user.timezone || 'Australia/Melbourne',
    });

    const submit: FormEventHandler = (e) => {
        e.preventDefault();

        patch(route('profile.update'), {
            preserveScroll: true,
        });
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Profile settings" />

            <SettingsLayout>
                <div className="space-y-6">
                    <HeadingSmall title="Profile information" description="Update your name and email address" />

                    <form onSubmit={submit} className="space-y-6">
                        <div className="grid gap-2">
                            <Label htmlFor="name">Name</Label>

                            <Input
                                id="name"
                                className="mt-1 block w-full"
                                value={data.name}
                                onChange={(e) => setData('name', e.target.value)}
                                required
                                autoComplete="name"
                                placeholder="Full name"
                            />

                            <InputError className="mt-2" message={errors.name} />
                        </div>

                        <div className="grid gap-2">
                            <Label htmlFor="email">Email address</Label>

                            <Input
                                id="email"
                                type="email"
                                className="mt-1 block w-full"
                                value={data.email}
                                onChange={(e) => setData('email', e.target.value)}
                                required
                                autoComplete="username"
                                placeholder="Email address"
                            />

                            <InputError className="mt-2" message={errors.email} />
                        </div>

                        <div className="grid gap-2">
                            <Label htmlFor="timezone">Timezone</Label>
                            <select
                                id="timezone"
                                className="mt-1 block w-full border rounded px-2 py-2 text-sm"
                                value={data.timezone}
                                onChange={(e) => setData('timezone', e.target.value)}
                                required
                            >
                                {australianTimezones.map(tz => (
                                    <option key={tz} value={tz}>{tz}</option>
                                ))}
                                <option disabled>──────────</option>
                                {otherTimezones.map(tz => (
                                    <option key={tz} value={tz}>{tz}</option>
                                ))}
                            </select>
                            <InputError className="mt-2" message={errors.timezone} />
                        </div>

                        {mustVerifyEmail && auth.user.email_verified_at === null && (
                            <div>
                                <p className="text-muted-foreground -mt-4 text-sm">
                                    Your email address is unverified.{' '}
                                    <Link
                                        href={route('verification.send')}
                                        method="post"
                                        as="button"
                                        className="text-foreground underline decoration-neutral-300 underline-offset-4 transition-colors duration-300 ease-out hover:decoration-current! dark:decoration-neutral-500"
                                    >
                                        Click here to resend the verification email.
                                    </Link>
                                </p>

                                {status === 'verification-link-sent' && (
                                    <div className="mt-2 text-sm font-medium text-green-600">
                                        A new verification link has been sent to your email address.
                                    </div>
                                )}
                            </div>
                        )}

                        <div className="flex items-center gap-4">
                            <Button disabled={processing}>Save</Button>

                            <Transition
                                show={recentlySuccessful}
                                enter="transition ease-in-out"
                                enterFrom="opacity-0"
                                leave="transition ease-in-out"
                                leaveTo="opacity-0"
                            >
                                <p className="text-sm text-neutral-600">Saved</p>
                            </Transition>
                        </div>
                    </form>
                </div>

                <DeleteUser />

                {demo_mode && (
                    <div className="mt-12 space-y-6">
                        <header>
                            <h3 className="mb-0.5 text-base font-medium">Reset demo account</h3>
                            <p className="text-muted-foreground text-sm">
                                Reset your demo data to the original state. This will delete all your current patients and restore the default demo data.
                            </p>
                        </header>
                        <div className="space-y-4 rounded-lg border border-yellow-100 bg-yellow-50 p-4 dark:border-yellow-200/10 dark:bg-yellow-700/10">
                            <div className="relative space-y-0.5 text-yellow-800 dark:text-yellow-100">
                                <p className="font-medium">Warning</p>
                                <p className="text-sm">
                                    Please proceed with caution. This will delete all your current demo data and cannot be undone.
                                </p>
                            </div>
                            <form
                                onSubmit={async (e) => {
                                    e.preventDefault();
                                    setResetting(true);
                                    setResetStatus(null);
                                    try {
                                        await axios.post(route('settings.reset-demo-data'));
                                        setResetStatus('Demo data has been reset!');
                                    } catch (err) {
                                        setResetStatus('Failed to reset demo data.');
                                    } finally {
                                        setResetting(false);
                                    }
                                }}
                            >
                                <button
                                    type="submit"
                                    className="inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-[color,box-shadow] disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive shadow-xs h-9 px-4 py-2 has-[>svg]:px-3 bg-yellow-500 hover:bg-yellow-600 text-white"
                                    disabled={resetting}
                                >
                                    {resetting ? 'Resetting...' : 'Reset demo data'}
                                </button>
                            </form>
                            {resetStatus && <p className="mt-2 text-sm text-green-600">{resetStatus}</p>}
                        </div>
                    </div>
                )}
            </SettingsLayout>
        </AppLayout>
    );
}
